hex_string = ""
byte_array = bytes.fromhex(hex_string)

try:
  decoded_utf8 = byte_array.decode("utf-8")
  print(f"UTF-8: {decoded_utf8}")
except UnicodeDecodeError:
  print("UTF-8: Decoding failed")

try:
  decoded_ascii = byte_array.decode("ascii")
  print(f"ASCII: {decoded_ascii}")
except UnicodeDecodeError:
  print("ASCII: Decoding failed")

try:
  decoded_latin1 = byte_array.decode("latin-1")
  print(f"Latin-1: {decoded_latin1}")
except UnicodeDecodeError:
  print("Latin-1: Decoding failed")
